import React, { useState } from 'react';
import { useGigUser } from 'redux/hooks/useGigUser';
import { useAllNewUserMessages } from 'redux/hooks/useMessages';
import ScrollToBottom from 'react-scroll-to-bottom';
import { List, ListItem, Input } from 'semantic-ui-react';
const NotificationsScrollable = (props) => {
	const giguser = useGigUser();
	const messages = useAllNewUserMessages(giguser.id);
	console.log(' MMM NNN ', messages);
	const [text, setText] = useState('');
	return (
		<>
			<ScrollToBottom>
				<List>
					{messages
						? messages.map((message) => (
								<ListItem>
									<List.Content>
										<List.Description>{message.content}</List.Description>
									</List.Content>
								</ListItem>
						  ))
						: ''}
				</List>
			</ScrollToBottom>

			<Input placeholder="Enter Message" action fluid>
				<input value={text} onChange={(e) => setText(e.target.value)} />
				<Button
					onClick={() => {
						sendTheMessage(text);
						setText('');
					}}
				>
					Send Message
				</Button>
			</Input>
		</>
	);
};

export default NotificationsScrollable;
