import React, { useState } from 'react';
import { useGigUser } from 'redux/hooks/useGigUser';
import _ from 'lodash';
import { useAllNewUserMessages, useAllUserMessageNotifications } from 'redux/hooks/useMessages';
import ScrollToBottom from 'react-scroll-to-bottom';
import { List, ListItem, Input, Button } from 'semantic-ui-react';
import { Link } from 'react-router-dom';
const NotificationsScrollable = (props) => {
	const giguser = useGigUser();
	const allMessages = useAllNewUserMessages(giguser.id);
	console.log('ALL Messages ', allMessages);
	const messageNotifications = _.uniqBy(allMessages, (message) => 'senderName');
	console.log(' MMM NNN ', allMessages);
	const getConversationMessages = (messageId) => {};
	return (
		<>
			<ScrollToBottom>
				<List>
					{messageNotifications
						? messageNotifications.map((message) => (
								<Link onClick={() => getConversationMessages(message.msgId)}>
									<ListItem>
										<List.Content>
											<List.Header>{message.senderName}</List.Header>
											<List.Description>{message.content}</List.Description>
										</List.Content>
									</ListItem>
								</Link>
						  ))
						: ''}
				</List>
			</ScrollToBottom>
		</>
	);
};

export default NotificationsScrollable;
