import { UPDATE_CHAT_MESSAGES } from '../actions/types';
const initialState = {};

export default function messagesReducer(state = initialState, action) {
	switch (action.type) {
		case UPDATE_CHAT_MESSAGES: {
			console.log(action.payload);
			return {
				...state,
				messages: action.payload,
			};
		}
		default:
			return state;
	}
}
