import gigety from 'apis/gigety';
import { updateUserMessageNotifications } from './messagesAction';
import { GET_CURRENT_USR_ACCOUNT, GET_ERRORS, UPDATE_ACTIVE_CONTACT, UPDATE_CONTACT_LIST } from './types';

export const findUserContacts = (userId) => async (dispatch) => {
	try {
		const response = await gigety.get(`/contacts/${userId}`);
		dispatch({
			type: UPDATE_CONTACT_LIST,
			payload: response.data,
		});
	} catch (error) {
		console.error('ERROR :: ', error);
		dispatch({
			type: GET_ERRORS,
			payload: error,
		});
	}
};

export const findActiveContact = (userId) => {};
export const updateActiveContact = (giguser, contact) => async (dispatch, getState) => {
	try {
		console.log('found active contact :: ', contact);
		try {
			const response = await gigety.post('/userAccount/contacts/setActive', contact);
			const ua = response.data;
			dispatch({
				type: GET_CURRENT_USR_ACCOUNT,
				payload: ua,
			});
		} catch (error) {
			console.warn(error);
		}

		dispatch(updateUserMessageNotifications());
	} catch (error) {
		console.error('ERROR :: ', error);
		dispatch({
			type: GET_ERRORS,
			payload: error,
		});
	}
};
