import { StompClientContext } from 'contexts/StompClientContext';
import { useCallback } from 'react';
import { useContext, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
	findMessagesFor121Chat,
	findNewUserMessages,
	findUserMessageNotifications,
	updateChatMessages,
} from '../actions/messagesAction';

export const use121ChatMessages = (senderId, recipientId) => {
	const content = useSelector((state) => state.messages);
	const dispatch = useDispatch();
	useEffect(() => {
		dispatch(findMessagesFor121Chat(senderId, recipientId));
	}, [dispatch, senderId, recipientId]);
	return content.messages;
};

export const useAllUserMessageNotifications = (userId) => {
	const content = useSelector((state) => state.messages);
	const dispatch = useDispatch();
	useEffect(() => {
		dispatch(findUserMessageNotifications(userId));
	}, [dispatch, userId]);
	return content.messageNotifications;
};

export const useAllNewUserMessages = (userId) => {
	const content = useSelector((state) => state.messages);
	console.log(content);
	const dispatch = useDispatch();
	useEffect(() => {
		dispatch(findNewUserMessages(userId));
	}, [dispatch, userId]);
	return content.newMessages;
};
export const useMessenger = (giguser, contact, sendChatMessage) => {
	const dispatch = useDispatch();
	const { getStompClient } = useContext(StompClientContext);
	const onMessageRecieved = useCallback(
		(msg) => {
			console.log('RECIIIIIIIIEEEEWEEEVED');
			const notification = JSON.parse(msg.body);
			if (contact.contactId === notification.senderId) {
				console.log('WE FOUND A MATCH');
				dispatch(findMessagesFor121Chat(giguser.id, notification.senderId));
			}
		},
		[contact.contactId, giguser.id, dispatch]
	);
	useEffect(() => {
		const stompClient = getStompClient();
		sendChatMessage.current = (message) => {
			console.log('SENDINGGGGGGGGGGGGGGGGGGGGGGGGGG');
			stompClient.publish({ destination: '/msg/chat', body: JSON.stringify(message) });
			dispatch(updateChatMessages(message));
		};

		console.log('Subscribing ... ');
		const { id } = stompClient.subscribe(`/user/${giguser.id}/queue}/messages`, onMessageRecieved);
		console.log(id + stompClient.connected);
		return () => {
			console.log('Might Unsubscribe');
			if (stompClient.connected) {
				console.log('UNSUBSCRIBING ...');
				stompClient.unsubscribe(id);
			}
		};
	}, [giguser, contact.contactId, getStompClient, dispatch, sendChatMessage, onMessageRecieved]);
};
