import React, { useState } from 'react';
import { useGigUser } from 'redux/hooks/useGigUser';
import { useAllNewUserMessages, useAllUserMessageNotifications } from 'redux/hooks/useMessages';
import ScrollToBottom from 'react-scroll-to-bottom';
import { List, ListItem, Input, Button } from 'semantic-ui-react';
const NotificationsScrollable = (props) => {
	const giguser = useGigUser();
	const messages = useAllNewUserMessages(giguser.id);
	console.log(' MMM NNN ', messages);
	return (
		<>
			<ScrollToBottom>
				<List>
					{messages
						? messages.map((message) => (
								<ListItem>
									<List.Content>
										<List.Description>{message.content}</List.Description>
									</List.Content>
								</ListItem>
						  ))
						: ''}
				</List>
			</ScrollToBottom>
		</>
	);
};

export default NotificationsScrollable;
