import React, { useState, useContext, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useGigUser } from 'redux/hooks/useGigUser';
import { useAllNewUserMessages } from 'redux/hooks/useMessages';
import { Container, Grid, GridColumn } from 'semantic-ui-react';
import ChatMessenger from '../ChatMessenger/ChatMessenger';
import ContactList from '../ContactList/ContactList';
import MessageInput from '../MessageInput/MessageInput';
import NotificationsScrollable from '../NotificationsScrollable/NotificationsScrollable';
import { StompClientContext } from 'contexts/StompClientContext';
const MessengerPage = ({ activeContact }) => {
	//const { giguser } = useSelector((state) => state.giguser);
	const giguser = useGigUser();
	console.log(giguser);
	const messages = useAllNewUserMessages(giguser.id);
	const [text, setText] = useState('');
	console.log('Founcd some messages :: ', messages);
	const dispatch = useDispatch();
	const { stompClient, sendChatMessage } = useContext(StompClientContext);
	useEffect(() => {
		const onMessageRecieved = (msg) => {
			const notification = JSON.parse(msg.body);
			if (profile.userId === notification.senderId) {
				dispatch(findMessagesFor121Chat(giguser.id, notification.senderId));
			}
		};
		const id = stompClient.subscribe(`/user/${giguser.id}/queue/messages`, onMessageRecieved);
		//const id = stompClient.subscribe(`/queue/messages/${profile.id}`, onMessageRecieved);
		console.log(id);

		return () => {
			console.log(`here we unsubscibe to id ${id}, you best check this is proper way to unsubscribe`);
			stompClient.unsubscribe(id);
		};
	}, [giguser, profile, stompClient, dispatch]);
	const sendTheMessage = (msg) => {
		if (msg.trim() !== '') {
			const message = {
				senderId: giguser.id,
				recipientId: profile.userId,
				senderName: giguser.name,
				recipientName: profile.email,
				content: msg,
				timestamp: new Date(),
			};
			console.log('sending msg: ', msg);
			sendChatMessage(message);
			console.log('msg sent', message);
		}
	};
	return (
		<Container fluid>
			<Grid celled>
				<Grid.Row>
					<GridColumn width={4}>
						<NotificationsScrollable giguser={giguser} />
					</GridColumn>
					<GridColumn width={6}>
						<ChatMessenger></ChatMessenger>
					</GridColumn>
				</Grid.Row>
				<Grid.Row>
					<Grid.Column width={4}>
						<ContactList></ContactList>
					</Grid.Column>
					<Grid.Column width={6}>
						<MessageInput text={text} setText={setText}></MessageInput>
					</Grid.Column>
				</Grid.Row>
			</Grid>
		</Container>
	);
};

export default MessengerPage;
