import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Button, Input } from 'semantic-ui-react';

const MessageInput = ({ activeContact, giguser }) => {
	const [text, setText] = useState('');
	const sendMessage = (msg) => {
		if (msg.trim() !== '') {
			const message = {
				senderId: giguser.id,
				recipientId: activeContact.userId,
				senderName: giguser.name,
				recipientName: activeContact.email,
				content: msg,
				timestamp: new Date(),
			};
			console.log('sending msg: ', msg);
			sendMessage(message);
			console.log('msg sent', message);
		}
	};
	return (
		<Input placeholder="Enter Message" action fluid>
			<input value={text} onChange={(e) => setText(e.target.value)} />
			<Button
				onClick={() => {
					sendMessage(text);
					setText('');
				}}
			>
				Send Message
			</Button>
		</Input>
	);
};

MessageInput.propTypes = {};

export default MessageInput;
